CREATE TABLE users (id int NOT NULL AUTO_INCREMENT, userName varchar(30) NOT NULL UNIQUE, email varchar(30) CHECK (email like '%@%'), password varchar(30) NOT NULL, dateofbirth date, PRIMARY KEY (Id));
CREATE TABLE cookies (id int NOT NULL AUTO_INCREMENT, cookie varchar(200) NOT NULL UNIQUE, active boolean NOT NULL DEFAULT TRUE, PRIMARY KEY (Id));
CREATE TABLE metadata (cookie_id int NOT NULL REFERENCES cookies(id), user_id int NOT NULL, time_added timestamp);
ALTER TABLE metadata ADD FOREIGN KEY (user_id) REFERENCES users(id);
